.deploy-frame #content {
    padding-top: 10px;
}
.deploy-frame .last-build,
.deploy-frame .new-build {
    background-color: #FFF;
    padding: 20px;
}
.deploy-frame .new-build {
    margin-top: 15px;
}
.deploy-frame .last-build .time {
    text-align: center;
    line-height: 30px;
    font-weight: bold;
    margin: 10px 0 5px 0;
}
.deploy-frame .last-build h2,
.deploy-frame .new-build h2 {
    margin: 0 0 10px 0;
    padding: 0;
    font-size: 18px;
}
.deploy-frame .platforms {
    display: -webkit-flex;
    display: flex;
    -webkit-flex-flow: row wrap;
    flex-flow: row wrap;
    align-content: space-between;
    justify-content: center;
    margin: 0 -20px 0 -20px;
}
.deploy-frame .platforms .platform {
    -webkit-flex-grow: 0;
    -ms-flex-grow: 0;
    flex-grow: 0;
    -webkit-flex-shrink: 0;
    -ms-flex-shrink: 0;
    flex-shrink: 0;
    background: #e1e1e1;
    text-align: center;
    margin: 10px;
    width: 120px;
    height: 120px;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: column;
    -ms-flex-direction: column;
    flex-direction: column;
    border-radius: 5px;
}
.deploy-frame .platforms .platform .section {
    -webkit-flex-grow: 1;
    -ms-flex-grow: 1;
    flex-grow: 1;
}
.deploy-frame .platforms .platform .section.title {
    -webkit-flex-grow: 0;
    -ms-flex-grow: 0;
    flex-grow: 0;
    height: 40px;
    font-size: 16px;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: column;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-justify-content: center;
    justify-content: center;
    margin-bottom: 5px;
}
.deploy-frame .platforms .platform.winphone .section.title {
    font-size: 14px;
}
.deploy-frame .platforms .platform .section.progress {
    height: 20px;
    -webkit-flex-grow: 0;
    -ms-flex-grow: 0;
    flex-grow: 0;
    -webkit-flex-shrink: 0;
    -ms-flex-shrink: 0;
    flex-shrink: 0;
    margin: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
/*.deploy-frame .platforms .platform:first-child {*/
    /*margin-left: 0;*/
/*}*/
/*.deploy-frame .platforms .platform:last-child {*/
    /*margin-right: 0;*/
/*}*/
.deploy-frame .platforms .platform h3 {
    font-size: 16px;
    font-weight: 300;
}
.deploy-frame .platforms .platform .platform-icon {
    font-size: 32px;
}
.deploy-frame .download-link {
    text-align: center;
}
.deploy-frame .download-link .download-link-text {
    width: 100%;
    display: block;
    margin: 10px auto 15px auto;
    border-radius: 5px;
    border: solid 1px #e1e1e1;
    line-height: 30px;
    text-align: center;
    padding: 0 10px;
}
.deploy-frame .platforms .platform .icon-spin {
    width: 12px;
    height: 12px;
    display: inline-block;
    text-align: center;
}
.deploy-frame .or {
    height: 1px;
    background-color: #CCC;
    position: relative;
    margin: 20px -10px 5px -10px;
    text-align: center;
    clear: both;
}
.deploy-frame .or .text {
    background-color: #ecedee;
    height: 25px;
    top: -10px;
    width: 10%;
    left: 45%;
    position: absolute;
    text-align: center;
    color: #999;
    text-shadow: 1px 1px 1px #CCC;
}
.deploy-frame .new-build .description {
    color: #999;
}
.deploy-frame .new-build .controls {
    text-align: center;
    margin-top: 20px;
}
