config.content:
.animateFull.ng-enter {
   transition: none;
}

.btn:hover {
  color:#BBBBBB;
}

.btn:focus {
	outline: none !important;
  color: #888888;
}

.navbar .btn, .panel .btn {
	border:0px;
  	background:none;
  	font-size:30px;
}

.tab-button.selected {
	color:red;
}

.add{
  color: #FFFFFF;  
}

section.static-item {
  height:115.5px;
}

#addButton, #confirmButton {
  display:inline-block;
      position: absolute;
    right: 40px;
}

#removeButton, #cancelButton{
  display:inline-block;
      position: absolute;
    left: 40px;
}

#mainTitle {
    position: absolute;
    bottom: 50%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%);
    color: #FFFFFF;
}

#mainTitleContainer {
  background-color: #617487;
}

var newItem = {};
newItem = JSON.parse(JSON.stringify(mdm.store.addAppSchema));
if(mdm.store.data__.variables.appSchemaURLs==undefined) {
	mdm.store.data__.variables.appSchemaURLs = [];
};
mdm.store.data__.variables.appSchemaURLs.push(newItem);
mdm.store.addAppSchema.importName = "";
mdm.store.addAppSchema.importCode = "";

var user = confirm("You are about to remove all App Schemas from the list.\nPress OK if you are sure you want to do this.");
if (user == true) {
	mdm.store.data__.variables.appSchemaURLs = [];
};

