function(modelValue, viewValue) {
  var urls = mdm.store.data__.variables.appSchemaURLs;
  if (urls != undefined) {
    for (var i = 0; i < urls.length; i++) {
      var url = urls[i];
      if (url.importName == viewValue) {
          return false;  
      }
    }
  }
  return true;
}

